import { Pool } from 'pg';
import { drizzle } from 'drizzle-orm/node-postgres';
import * as schema from "@shared/schema";

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle(pool, { schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/openai.ts.content:
import { GoogleGenAI } from "@google/genai";

// Using Gemini 1.5 Flash
const genAI = new GoogleGenAI({ 
  apiKey: process.env.GEMINI_API_KEY || ""
});

export interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
}

export interface SiteAnalysis {
  suitabilityScore: number;
  factors: {
    renewableAccess: number;
    transportCost: string;
    demandProximity: string;
    waterAvailability: string;
    regulatorySupport: string;
  };
  recommendations: string[];
  co2SavedAnnually: number;
  industriesSupported: number;
  renewableUtilization: number;
}

export async function chatWithAssistant(messages: ChatMessage[]): Promise<string> {
  try {
    const systemPrompt = `You are InfraVision, an AI assistant specialized in green hydrogen infrastructure planning. You help users understand:

1. Optimal locations for hydrogen production plants
2. Suitability scoring factors and methodologies
3. Renewable energy integration strategies
4. Infrastructure planning best practices
5. Environmental and economic impact calculations

Provide helpful, accurate, and actionable advice about green hydrogen infrastructure. Keep responses concise but informative. Focus on practical insights that help with decision-making.`;

    // Convert messages to Gemini format and include system prompt
    const geminiMessages = messages.map(msg => `${msg.role === 'user' ? 'User' : 'Assistant'}: ${msg.content}`).join('\n\n');
    const fullPrompt = `${systemPrompt}\n\nConversation:\n${geminiMessages}\n\nAssistant:`;

    const result = await genAI.models.generateContent({
      model: "gemini-1.5-flash",
      contents: [{ role: "user", parts: [{ text: fullPrompt }] }]
    });

    const text = result.candidates?.[0]?.content?.parts?.[0]?.text || "I apologize, but I couldn't process your request at the moment.";

    return text || "I apologize, but I couldn't process your request at the moment.";
  } catch (error) {
    console.error("Gemini API error:", error);
    throw new Error("Failed to get response from AI assistant");
  }
}

export async function analyzeSiteLocation(
  latitude: number,
  longitude: number,
  nearbyRenewables: Array<{ type: string; distance: number; capacity: number }>,
  nearbyDemand: Array<{ type: string; distance: number; level: string }>
): Promise<SiteAnalysis> {
  // Calculate location-based scoring using actual data
  return calculateLocationSuitability(latitude, longitude, nearbyRenewables, nearbyDemand);
}

function calculateLocationSuitability(
  latitude: number,
  longitude: number,
  nearbyRenewables: Array<{ type: string; distance: number; capacity: number }>,
  nearbyDemand: Array<{ type: string; distance: number; level: string }>
): SiteAnalysis {

  // 1. Renewable Access Score (0-100)
  let renewableScore = 0;
  let totalCapacity = 0;
  let renewableUtilization = 0;

  if (nearbyRenewables.length > 0) {
    nearbyRenewables.forEach(r => {
      // Closer renewables get higher scores, with capacity weighting
      const distanceScore = Math.max(0, 100 - (r.distance * 2)); // 2 points per km
      const capacityBonus = Math.min(50, r.capacity / 10); // Up to 50 bonus for capacity
      renewableScore += (distanceScore + capacityBonus) / nearbyRenewables.length;
      totalCapacity += r.capacity;
    });
    renewableUtilization = Math.min(95, 40 + (totalCapacity / 50));
  } else {
    renewableScore = 20; // Base score for areas without nearby renewables
    renewableUtilization = 25;
  }

  // 2. Demand Proximity Score (0-100)
  let demandScore = 0;
  let industriesSupported = 0;

  if (nearbyDemand.length > 0) {
    nearbyDemand.forEach(d => {
      const distanceScore = Math.max(0, 100 - (d.distance * 1.5)); // 1.5 points per km
      const demandMultiplier = d.level === 'High' ? 1.5 : d.level === 'Medium' ? 1.2 : 1.0;
      demandScore += (distanceScore * demandMultiplier) / nearbyDemand.length;
      industriesSupported += d.level === 'High' ? 3 : d.level === 'Medium' ? 2 : 1;
    });
  } else {
    demandScore = 25; // Base score for areas without nearby demand
    industriesSupported = 2;
  }

  // 3. Location-based factors
  const isCoastal = latitude >= 8 && latitude <= 37 && (longitude <= 75 || longitude >= 80); // India coastal regions
  const isIndustrial = nearbyDemand.some(d => d.type.includes('Steel') || d.type.includes('Chemical') || d.type.includes('Refinery'));
  const isRenewableRich = totalCapacity > 400;

  // 4. Calculate overall suitability score
  const locationBonus = isCoastal ? 10 : 0;
  const industrialBonus = isIndustrial ? 15 : 0;
  const renewableBonus = isRenewableRich ? 10 : 0;

  const suitabilityScore = Math.min(100, Math.max(20, 
    (renewableScore * 0.4) + 
    (demandScore * 0.4) + 
    locationBonus + 
    industrialBonus + 
    renewableBonus
  ));

  // 5. Calculate environmental impact
  const baseC02Savings = 15000 + (suitabilityScore * 2000) + (totalCapacity * 100);
  const co2SavedAnnually = Math.round(baseC02Savings + (Math.random() * 5000)); // Add some variation

  // 6. Generate transport cost assessment
  const avgDistance = nearbyDemand.length > 0 ? 
    nearbyDemand.reduce((sum, d) => sum + d.distance, 0) / nearbyDemand.length : 100;
  const transportCost = avgDistance < 50 ? "Low" : avgDistance < 100 ? "Medium" : "High";

  // 7. Generate recommendations
  const recommendations = [];
  if (renewableScore < 40) recommendations.push("Consider establishing renewable energy partnerships or grid connections");
  if (demandScore < 40) recommendations.push("Identify and develop relationships with potential industrial customers");
  if (isCoastal) recommendations.push("Leverage coastal location for hydrogen export opportunities");
  if (isRenewableRich) recommendations.push("Maximize utilization of abundant renewable energy sources");
  if (recommendations.length === 0) recommendations.push("Site shows good potential for hydrogen infrastructure development");

  return {
    suitabilityScore: Math.round(suitabilityScore),
    factors: {
      renewableAccess: Math.round(renewableScore),
      transportCost,
      demandProximity: demandScore > 70 ? "Excellent" : demandScore > 50 ? "Good" : demandScore > 30 ? "Fair" : "Limited",
      waterAvailability: isCoastal ? "Excellent" : latitude > 15 && latitude < 30 ? "Good" : "Moderate",
      regulatorySupport: isIndustrial ? "Strong" : "Moderate"
    },
    recommendations,
    co2SavedAnnually,
    industriesSupported: Math.min(20, industriesSupported),
    renewableUtilization: Math.round(renewableUtilization)
  };
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
// Removed Replit Auth - using Supabase instead
import { chatWithAssistant, analyzeSiteLocation, type ChatMessage } from "./openai";
import { insertHydrogenSiteSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Since we're using Supabase for auth, we don't need server-side auth routes

  // Hydrogen sites routes
  app.post('/api/hydrogen-sites', async (req: any, res) => {
    try {
      // For demo purposes, we'll use a default user ID
      const userId = "demo-user";
      const siteData = insertHydrogenSiteSchema.parse({
        ...req.body,
        userId
      });

      // Get nearby infrastructure for AI analysis
      const renewables = await storage.getRenewableSources();
      const demandCenters = await storage.getDemandCenters();

      // Calculate distances and find nearby infrastructure
      const nearbyRenewables = renewables
        .map(r => ({
          type: r.type,
          distance: calculateDistance(
            parseFloat(siteData.latitude), 
            parseFloat(siteData.longitude),
            parseFloat(r.latitude), 
            parseFloat(r.longitude)
          ),
          capacity: r.capacity || 100
        }))
        .filter(r => r.distance < 100) // Within 100km
        .slice(0, 5);

      const nearbyDemand = demandCenters
        .map(d => ({
          type: d.type,
          distance: calculateDistance(
            parseFloat(siteData.latitude), 
            parseFloat(siteData.longitude),
            parseFloat(d.latitude), 
            parseFloat(d.longitude)
          ),
          level: d.demandLevel
        }))
        .filter(d => d.distance < 150) // Within 150km
        .slice(0, 5);

      // Get AI analysis
      const analysis = await analyzeSiteLocation(
        parseFloat(siteData.latitude),
        parseFloat(siteData.longitude),
        nearbyRenewables,
        nearbyDemand
      );

      // Create site with AI-calculated values
      const site = await storage.createHydrogenSite({
        ...siteData,
        suitabilityScore: analysis.suitabilityScore,
        co2SavedAnnually: analysis.co2SavedAnnually,
        industriesSupported: analysis.industriesSupported,
        renewableUtilization: analysis.renewableUtilization
      });

      res.json({ site, analysis });
    } catch (error) {
      console.error("Error creating hydrogen site:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid site data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create hydrogen site" });
    }
  });

  app.get('/api/hydrogen-sites', async (req: any, res) => {
    try {
      // For demo purposes, use default user ID
      const userId = "demo-user";
      const sites = await storage.getHydrogenSites(userId);
      res.json(sites);
    } catch (error) {
      console.error("Error fetching hydrogen sites:", error);
      res.status(500).json({ message: "Failed to fetch hydrogen sites" });
    }
  });

  app.delete('/api/hydrogen-sites/:id', async (req: any, res) => {
    try {
      // For demo purposes, use default user ID
      const userId = "demo-user";
      const siteId = req.params.id;
      await storage.deleteHydrogenSite(siteId, userId);
      res.json({ message: "Site deleted successfully" });
    } catch (error) {
      console.error("Error deleting hydrogen site:", error);
      res.status(500).json({ message: "Failed to delete hydrogen site" });
    }
  });

  // AI suggestions route
  app.get('/api/ai-suggestions', async (req, res) => {
    try {
      const aiSites = await storage.getAiSuggestedSites();
      res.json(aiSites);
    } catch (error) {
      console.error("Error fetching AI suggestions:", error);
      res.status(500).json({ message: "Failed to fetch AI suggestions" });
    }
  });

  // Infrastructure data routes
  app.get('/api/renewable-sources', async (req, res) => {
    try {
      const sources = await storage.getRenewableSources();
      res.json(sources);
    } catch (error) {
      console.error("Error fetching renewable sources:", error);
      res.status(500).json({ message: "Failed to fetch renewable sources" });
    }
  });

  app.get('/api/demand-centers', async (req, res) => {
    try {
      const centers = await storage.getDemandCenters();
      res.json(centers);
    } catch (error) {
      console.error("Error fetching demand centers:", error);
      res.status(500).json({ message: "Failed to fetch demand centers" });
    }
  });

  // Chatbot route
  app.post('/api/chat', async (req, res) => {
    try {
      const { messages }: { messages: ChatMessage[] } = req.body;

      if (!messages || !Array.isArray(messages)) {
        return res.status(400).json({ message: "Invalid messages format" });
      }

      const response = await chatWithAssistant(messages);
      res.json({ response });
    } catch (error) {
      console.error("Error in chat endpoint:", error);
      res.status(500).json({ message: "Failed to get chat response" });
    }
  });

  // Site analysis route
  app.post('/api/analyze-site', async (req, res) => {
    try {
      const { latitude, longitude } = req.body;

      if (!latitude || !longitude) {
        return res.status(400).json({ message: "Latitude and longitude are required" });
      }

      // Get nearby infrastructure
      const renewables = await storage.getRenewableSources();
      const demandCenters = await storage.getDemandCenters();

      const nearbyRenewables = renewables
        .map(r => ({
          type: r.type,
          distance: calculateDistance(latitude, longitude, parseFloat(r.latitude), parseFloat(r.longitude)),
          capacity: r.capacity || 100
        }))
        .filter(r => r.distance < 100)
        .slice(0, 5);

      const nearbyDemand = demandCenters
        .map(d => ({
          type: d.type,
          distance: calculateDistance(latitude, longitude, parseFloat(d.latitude), parseFloat(d.longitude)),
          level: d.demandLevel
        }))
        .filter(d => d.distance < 150)
        .slice(0, 5);

      const analysis = await analyzeSiteLocation(latitude, longitude, nearbyRenewables, nearbyDemand);
      res.json(analysis);
    } catch (error) {
      console.error("Error analyzing site:", error);
      res.status(500).json({ message: "Failed to analyze site" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

// Helper function to calculate distance between two coordinates
function calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
  const R = 6371; // Radius of the Earth in kilometers
  const dLat = deg2rad(lat2 - lat1);
  const dLon = deg2rad(lon2 - lon1);
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
    Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  const d = R * c; // Distance in kilometers
  return d;
}

function deg2rad(deg: number): number {
  return deg * (Math.PI/180);
}

server/storage.ts.content:
import {
  users,
  hydrogenSites,
  renewableSources,
  demandCenters,
  type User,
  type UpsertUser,
  type InsertHydrogenSite,
  type HydrogenSite,
  type RenewableSource,
  type DemandCenter,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Hydrogen site operations
  createHydrogenSite(site: InsertHydrogenSite): Promise<HydrogenSite>;
  getHydrogenSites(userId: string): Promise<HydrogenSite[]>;
  getAiSuggestedSites(): Promise<HydrogenSite[]>;
  deleteHydrogenSite(id: string, userId: string): Promise<void>;

  // Infrastructure data operations
  getRenewableSources(): Promise<RenewableSource[]>;
  getDemandCenters(): Promise<DemandCenter[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Replit Auth.

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Hydrogen site operations

  async createHydrogenSite(site: InsertHydrogenSite): Promise<HydrogenSite> {
    const [newSite] = await db
      .insert(hydrogenSites)
      .values(site)
      .returning();
    return newSite;
  }

  async getHydrogenSites(userId: string): Promise<HydrogenSite[]> {
    return await db
      .select()
      .from(hydrogenSites)
      .where(eq(hydrogenSites.userId, userId))
      .orderBy(desc(hydrogenSites.createdAt));
  }

  async getAiSuggestedSites(): Promise<HydrogenSite[]> {
    return await db
      .select()
      .from(hydrogenSites)
      .where(eq(hydrogenSites.isAiSuggested, true))
      .orderBy(desc(hydrogenSites.suitabilityScore));
  }

  async deleteHydrogenSite(id: string, userId: string): Promise<void> {
    await db
      .delete(hydrogenSites)
      .where(and(eq(hydrogenSites.id, id), eq(hydrogenSites.userId, userId)));
  }

  // Infrastructure data operations

  async getRenewableSources(): Promise<RenewableSource[]> {
    return await db.select().from(renewableSources);
  }

  async getDemandCenters(): Promise<DemandCenter[]> {
    return await db.select().from(demandCenters);
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  jsonb,
  pgTable,
  timestamp,
  varchar,
  text,
  integer,
  decimal,
  boolean,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table.
// (IMPORTANT) This table is mandatory for Replit Auth, don't drop it.
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table.
// (IMPORTANT) This table is mandatory for Replit Auth, don't drop it.
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Hydrogen plant sites table
export const hydrogenSites = pgTable("hydrogen_sites", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id).notNull(),
  name: varchar("name").notNull(),
  latitude: decimal("latitude", { precision: 10, scale: 8 }).notNull(),
  longitude: decimal("longitude", { precision: 11, scale: 8 }).notNull(),
  suitabilityScore: integer("suitability_score").notNull(),
  isAiSuggested: boolean("is_ai_suggested").default(false),
  co2SavedAnnually: integer("co2_saved_annually"),
  industriesSupported: integer("industries_supported"),
  renewableUtilization: integer("renewable_utilization"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Renewable energy sources table
export const renewableSources = pgTable("renewable_sources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  type: varchar("type").notNull(), // 'wind', 'solar', 'hydro'
  latitude: decimal("latitude", { precision: 10, scale: 8 }).notNull(),
  longitude: decimal("longitude", { precision: 11, scale: 8 }).notNull(),
  capacity: integer("capacity"), // MW
  createdAt: timestamp("created_at").defaultNow(),
});

// Demand centers table
export const demandCenters = pgTable("demand_centers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name").notNull(),
  type: varchar("type").notNull(), // 'steel', 'transport', 'chemical', 'power'
  latitude: decimal("latitude", { precision: 10, scale: 8 }).notNull(),
  longitude: decimal("longitude", { precision: 11, scale: 8 }).notNull(),
  demandLevel: varchar("demand_level").notNull(), // 'low', 'medium', 'high'
  createdAt: timestamp("created_at").defaultNow(),
});

export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;

export const insertHydrogenSiteSchema = createInsertSchema(hydrogenSites).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertRenewableSourceSchema = createInsertSchema(renewableSources).omit({
  id: true,
  createdAt: true,
});

export const insertDemandCenterSchema = createInsertSchema(demandCenters).omit({
  id: true,
  createdAt: true,
});

export type InsertHydrogenSite = z.infer<typeof insertHydrogenSiteSchema>;
export type HydrogenSite = typeof hydrogenSites.$inferSelect;
export type RenewableSource = typeof renewableSources.$inferSelect;
export type DemandCenter = typeof demandCenters.$inferSelect;

