import {
  Brain,
  Layers,
  Zap,
  Download,
  Calculator,
  Share,
  Wind,
  Sun,
  Building2,
  Factory,
  Truck,
  ChevronLeft,
  ChevronRight,
  X,
} from "lucide-react";

// Mock data for demonstration
const mockAiSuggestions = [
  {
    id: 1,
    name: "Solar Valley Plant",
    suitabilityScore: 85,
    renewableUtilization: 92,
    co2SavedAnnually: 25000,
  },
  {
    id: 2,
    name: "Wind Ridge Facility",
    suitabilityScore: 78,
    renewableUtilization: 88,
    co2SavedAnnually: 18000,
  },
  {
    id: 3,
    name: "Green Harbor Complex",
    suitabilityScore: 91,
    renewableUtilization: 95,
    co2SavedAnnually: 32000,
  },
];

interface SidebarProps {
  isOpen: boolean;
  onToggle: () => void;
  onSiteSelect: (site: any) => void;
}

export default function PerfectSidebar({ isOpen, onToggle, onSiteSelect }: SidebarProps) {
  const [activeSection, setActiveSection] = useState<string | null>(null);
  const [layerStates, setLayerStates] = useState({
    existingPlants: true,
    renewableSources: true,
    demandCenters: true,
    pipelineNetwork: false,
    regulatoryZones: false,
  });

  // Mock query for AI suggestions
  const { data: aiSuggestions = mockAiSuggestions } = useQuery({
    queryKey: ["/api/ai-suggestions"],
    queryFn: () => Promise.resolve(mockAiSuggestions),
    refetchOnWindowFocus: false,
  });

  const handleSiteSelect = (site: any) => {
    console.log("Selected site:", site);
    onSiteSelect(site);
  };

  const toggleLayer = (layer: keyof typeof layerStates) => {
    setLayerStates((prev) => ({
      ...prev,
      [layer]: !prev[layer],
    }));
  };

  const getScoreColor = (score: number) => {
    if (score >= 80) return "text-green-600 dark:text-green-400";
    if (score >= 60) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const getScoreLabel = (score: number) => {
    if (score >= 80) return "Excellent";
    if (score >= 60) return "Good";
    return "Poor";
  };

  const getRenewableIcon = (type: string) => {
    switch (type?.toLowerCase()) {
      case "wind":
        return <Wind className="w-3 h-3" />;
      case "solar":
        return <Sun className="w-3 h-3" />;
      default:
        return <Zap className="w-3 h-3" />;
    }
  };

  const toggleSidebar = () => {
    onToggle();
    setActiveSection(null);
  };

  const layers = [
    {
      key: "existingPlants",
      label: "Existing Hâ‚‚ Plants",
      icon: Factory,
      color: "text-blue-500",
    },
    {
      key: "renewableSources",
      label: "Renewable Sources",
      icon: Zap,
      color: "text-green-500",
    },
    {
      key: "demandCenters",
      label: "Demand Centers",
      icon: Building2,
      color: "text-purple-500",
    },
    {
      key: "pipelineNetwork",
      label: "Pipeline Network",
      icon: Truck,
      color: "text-orange-500",
    },
    {
      key: "regulatoryZones",
      label: "Regulatory Zones",
      icon: Building2,
      color: "text-gray-500",
    },
  ];

  return (
    <>
      {/* Toggle Button - Always visible */}
