      >
        {isOpen ? (
          <ChevronLeft className="w-4 h-4" />
        ) : (
          <ChevronRight className="w-4 h-4" />
        )}
      </Button>

      {/* Sidebar */}
      <div
        className={`fixed left-0 top-16 bottom-0 z-40 transition-all duration-300 ${isOpen ? "translate-x-0" : "-translate-x-full"}`}
      >
        <div className="w-80 h-full bg-background/95 backdrop-blur-sm border-r border-border shadow-xl">
          {/* Header */}
          <div className="p-4 border-b border-border bg-card/50">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold text-foreground">
                Control Panel
              </h2>
              <Button
                onClick={toggleSidebar}
                variant="ghost"
                size="sm"
                className="h-8 w-8 p-0"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto">
            {/* AI Suggestions Section */}
            <div className="p-4 border-b border-border">
              <div className="flex items-center justify-between mb-3">
                <h3 className="font-medium text-foreground flex items-center space-x-2">
                  <Brain className="w-4 h-4 text-blue-500" />
