                      onClick={() => handleSiteSelect(suggestion)}
                    >
                      <div className="flex items-start justify-between mb-2">
                        <h4 className="font-medium text-foreground text-sm group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
                          {suggestion.name}
                        </h4>
                        <div className="flex flex-col items-end">
                          <span
                            className={`text-xs px-2 py-1 rounded-full font-medium ${getScoreColor(suggestion.suitabilityScore)} bg-muted`}
                          >
                            {suggestion.suitabilityScore}
                          </span>
                          <span className="text-xs text-muted-foreground mt-1">
                            {getScoreLabel(suggestion.suitabilityScore)}
                          </span>
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-2 text-xs">
                        <div className="flex items-center space-x-1 text-green-600 dark:text-green-400">
                          <Zap className="w-3 h-3" />
