import { useState, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { X, Send, HelpCircle } from "lucide-react";

interface HelpFormProps {
  onClose: () => void;
}

export default function HelpForm({ onClose }: HelpFormProps) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    organization: '',
    queryType: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [toastType, setToastType] = useState<'success' | 'error'>('success');

  useEffect(() => {
    // Hide the map and other elements
    const mapElements = document.querySelectorAll('.leaflet-container, [class*="map"], [id*="map"]');
    const savedStyles: { element: Element; display: string; visibility: string }[] = [];

    mapElements.forEach((el) => {
      const element = el as HTMLElement;
      savedStyles.push({
        element,
        display: element.style.display,
        visibility: element.style.visibility
      });
      element.style.visibility = 'hidden';
      element.style.pointerEvents = 'none';
    });

    // Prevent body scroll
    document.body.style.overflow = 'hidden';
    document.documentElement.style.overflow = 'hidden';

    return () => {
      // Restore map visibility
      savedStyles.forEach(({ element, display, visibility }) => {
        const el = element as HTMLElement;
        el.style.display = display;
        el.style.visibility = visibility;
        el.style.pointerEvents = '';
      });
      document.body.style.overflow = '';
      document.documentElement.style.overflow = '';
    };
  }, []);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const showSuccessToast = (message: string) => {
    setToastMessage(message);
    setToastType('success');
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  const showErrorToast = (message: string) => {
    setToastMessage(message);
    setToastType('error');
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  const handleSubmit = async () => {
    if (!formData.name || !formData.email || !formData.queryType || !formData.message) {
      showErrorToast('Please fill in all required fields');
      return;
    }

    setIsSubmitting(true);

    try {
      const response = await fetch('https://formspree.io/f/mldwjzvg', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          organization: formData.organization,
          queryType: formData.queryType,
          message: formData.message,
          subject: `InfraVision Help Request - ${formData.queryType}`,
          _replyto: formData.email,
        }),
      });

      if (response.ok) {
        showSuccessToast("Thank you for reaching out! We'll get back to you within 24 hours.");

        setFormData({
          name: '',
          email: '',
          organization: '',
          queryType: '',
          message: ''
        });

        setTimeout(() => {
          onClose();
        }, 2000);
      } else {
        throw new Error('Failed to send message');
      }
    } catch (error) {
      showErrorToast('Please try again or contact us directly at support@infravision.com');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      {/* Full screen overlay that completely covers everything */}
      <div 
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          backgroundColor: 'rgba(0, 0, 0, 0.9)',
          zIndex: 999999999,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px',
          boxSizing: 'border-box'
        }}
        onClick={(e) => {
          if (e.target === e.currentTarget) {
            onClose();
          }
        }}
      >
        {/* Modal Card */}
        <div
          style={{
            width: '100%',
            maxWidth: '700px',
            maxHeight: '90vh',
            backgroundColor: 'white',
            borderRadius: '16px',
            boxShadow: '0 25px 50px rgba(0, 0, 0, 0.8)',
            overflow: 'hidden',
            border: '4px solid #2563eb',
            zIndex: 999999999
          }}
          onClick={(e) => e.stopPropagation()}
        >
          {/* Header */}
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            padding: '24px',
            backgroundColor: '#eff6ff',
            borderBottom: '3px solid #2563eb'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <div style={{ 
                backgroundColor: '#2563eb', 
                borderRadius: '50%', 
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <HelpCircle className="w-6 h-6 text-white" />
              </div>
              <h2 style={{ 
                fontSize: '24px', 
                fontWeight: 'bold', 
                color: '#1e40af', 
                margin: 0 
              }}>
                Help & Support
              </h2>
            </div>
            <button
              onClick={onClose}
              style={{
                background: 'none',
                border: 'none',
                padding: '8px',
                borderRadius: '8px',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: '#fee2e2',
                color: '#dc2626'
              }}
              data-testid="button-close-help"
            >
              <X className="w-5 h-5" />
            </button>
          </div>

          {/* Content */}
          <div style={{ 
            padding: '24px', 
            backgroundColor: 'white',
            maxHeight: 'calc(90vh - 120px)',
            overflowY: 'auto'
          }}>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '24px' }}>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '16px' }}>
                <div>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                    Full Name *
                  </label>
                  <input
                    type="text"
                    placeholder="Enter your full name"
                    value={formData.name}
                    name="name"
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    data-testid="input-name"
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '14px',
                      outline: 'none',
                      transition: 'border-color 0.2s'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                    onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                  />
                </div>

                <div>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                    Email Address *
                  </label>
                  <input
                    type="email"
                    placeholder="your.email@company.com"
                    value={formData.email}
                    name="email"
                    onChange={(e) => handleInputChange('email', e.target.value)}
                    data-testid="input-email"
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '14px',
                      outline: 'none',
                      transition: 'border-color 0.2s'
                    }}
                    onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                    onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                  />
                </div>
              </div>

              <div>
                <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                  Organization
                </label>
                <input
                  type="text"
                  placeholder="Company name or organization"
                  value={formData.organization}
                  name="organization"
                  onChange={(e) => handleInputChange('organization', e.target.value)}
                  data-testid="input-organization"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '2px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    transition: 'border-color 0.2s'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
              </div>

              <div>
                <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                  Query Type *
                </label>
                <select
                  value={formData.queryType}
                  name="queryType"
                  onChange={(e) => handleInputChange('queryType', e.target.value)}
                  data-testid="select-query-type"
                  style={{
                    width: '100%',
                    padding: '12px',
                    border: '2px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    backgroundColor: 'white',
                    cursor: 'pointer'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                >
                  <option value="">Select the type of help you need</option>
                  <option value="technical-support">Technical Support</option>
                  <option value="feature-request">Feature Request</option>
                  <option value="data-question">Data & Analytics Question</option>
                  <option value="account-billing">Account & Billing</option>
                  <option value="integration-help">Integration Help</option>
                  <option value="training-demo">Training & Demo Request</option>
                  <option value="partnership">Partnership Inquiry</option>
                  <option value="bug-report">Bug Report</option>
                  <option value="general">General Question</option>
                </select>
              </div>

              <div>
                <label style={{ display: 'block', fontSize: '14px', fontWeight: '500', marginBottom: '8px', color: '#374151' }}>
                  Detailed Message *
                </label>
                <textarea
                  placeholder="Please describe your question or issue in detail. Include any relevant information that might help us assist you better."
                  value={formData.message}
                  name="message"
                  onChange={(e) => handleInputChange('message', e.target.value)}
                  data-testid="textarea-message"
                  style={{
                    width: '100%',
                    minHeight: '120px',
                    padding: '12px',
                    border: '2px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '14px',
                    outline: 'none',
                    resize: 'none',
                    fontFamily: 'inherit'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#2563eb'}
                  onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                />
              </div>

              <div style={{
                backgroundColor: '#eff6ff',
                border: '2px solid #bfdbfe',
                borderRadius: '12px',
                padding: '20px'
              }}>
                <h4 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '12px', color: '#1e40af' }}>
                  Quick Help Options
                </h4>
                <div style={{ fontSize: '14px', color: '#1e40af', lineHeight: '1.6' }}>
                  <p style={{ margin: '6px 0' }}>â€¢ <strong>Documentation:</strong> Check our knowledge base at docs.infravision.com</p>
                  <p style={{ margin: '6px 0' }}>â€¢ <strong>Video Tutorials:</strong> Watch getting started videos</p>
                  <p style={{ margin: '6px 0' }}>â€¢ <strong>Community:</strong> Join our Slack community for peer support</p>
                  <p style={{ margin: '6px 0' }}>â€¢ <strong>Emergency Support:</strong> Call +91-800-HYDROGEN for urgent issues</p>
                </div>
              </div>

              <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '12px', paddingTop: '16px' }}>
                <button
                  onClick={onClose}
                  data-testid="button-cancel-help"
                  style={{
                    padding: '12px 24px',
                    border: '2px solid #d1d5db',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    color: '#374151',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    transition: 'all 0.2s'
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.backgroundColor = '#f3f4f6';
                    e.currentTarget.style.borderColor = '#9ca3af';
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.backgroundColor = 'white';
                    e.currentTarget.style.borderColor = '#d1d5db';
                  }}
                >
                  Cancel
                </button>
                <button
                  onClick={handleSubmit}
                  disabled={isSubmitting || !formData.name || !formData.email || !formData.queryType || !formData.message}
                  data-testid="button-submit-help"
                  style={{
                    padding: '12px 24px',
                    border: 'none',
                    borderRadius: '8px',
                    backgroundColor: isSubmitting || !formData.name || !formData.email || !formData.queryType || !formData.message ? '#9ca3af' : '#2563eb',
                    color: 'white',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: isSubmitting || !formData.name || !formData.email || !formData.queryType || !formData.message ? 'not-allowed' : 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    transition: 'all 0.2s'
                  }}
                  onMouseOver={(e) => {
                    if (!e.currentTarget.disabled) {
                      e.currentTarget.style.backgroundColor = '#1d4ed8';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (!e.currentTarget.disabled) {
                      e.currentTarget.style.backgroundColor = '#2563eb';
                    }
                  }}
                >
                  {isSubmitting ? (
                    'Sending...'
                  ) : (
                    <>
                      <Send className="w-4 h-4" />
                      Send Message
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Toast notification */}
      {showToast && (
        <div 
          style={{
            position: 'fixed',
            top: '20px',
            right: '20px',
            padding: '16px 20px',
            borderRadius: '12px',
            boxShadow: '0 20px 25px rgba(0, 0, 0, 0.4)',
            border: '2px solid',
            zIndex: 999999999,
            backgroundColor: toastType === 'success' ? '#10b981' : '#ef4444',
            borderColor: toastType === 'success' ? '#059669' : '#dc2626',
            color: 'white',
            fontSize: '14px',
            fontWeight: '500',
            maxWidth: '400px'
          }}
        >
          {toastMessage}
        </div>
      )}
    </>
  );
}

client/src/components/QuickActions.tsx.content:
import { useState } from 'react';
import { Button } from "@/components/ui/button";
import { 
  Factory, 
  Download, 
  Calculator, 
  Share,
  FileText,
  ExternalLink
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { usePDFReport } from "@/hooks/usePDFReport";

export default function QuickActions() {
  const { toast } = useToast();
  const [isCalculatorOpen, setIsCalculatorOpen] = useState(false);
  const { generateReport, isDataLoaded } = usePDFReport();

  const handleExportPDF = async () => {
    if (!isDataLoaded) {
      toast({
        title: "Data Loading",
        description: "Please wait for data to load before generating report.",
        variant: "destructive",
      });
      return;
    }

    try {
      toast({
        title: "Generating Report",
        description: "Creating comprehensive hydrogen infrastructure report...",
      });

      const filename = await generateReport();

      toast({
        title: "Report Generated",
        description: `Successfully generated ${filename}`,
      });
    } catch (error) {
      console.error('PDF generation error:', error);
      toast({
        title: "Export Failed", 
        description: "Failed to generate PDF report. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleShare = async () => {
    const shareData = {
      title: 'InfraVision - Green Hydrogen Infrastructure Planning',
      text: 'Check out this interactive map for planning green hydrogen infrastructure across India',
      url: window.location.href,
    };

    if (navigator.share) {
      try {
        await navigator.share(shareData);
      } catch (err) {
        // Fallback to clipboard copy
        await navigator.clipboard.writeText(window.location.href);
        toast({
          title: "Link Copied",
          description: "Project link has been copied to clipboard.",
        });
      }
    } else {
      // Fallback for browsers without Web Share API
      await navigator.clipboard.writeText(window.location.href);
      toast({
        title: "Link Copied",
        description: "Project link has been copied to clipboard.",
      });
    }
  };

  const openCalculator = () => {
    setIsCalculatorOpen(true);
    toast({
      title: "H2 Calculator",
      description: "Opening hydrogen production and cost calculator...",
    });

    // Open calculator in new window/modal
    window.open(
      '/calculator', 
      'h2calculator', 
      'width=800,height=600,resizable=yes,scrollbars=yes'
    );
  };

  return (
    <div className="fixed bottom-4 right-4 z-50 md:bottom-20">
      <div className="flex flex-col gap-2">

        {/* Export PDF Button */}
        <Button
          size="icon"
          variant="outline"
          className="w-12 h-12 bg-card border-border shadow-lg hover:bg-muted"
          onClick={handleExportPDF}
          data-testid="button-export-pdf"
        >
          <Download className="w-5 h-5" />
        </Button>

        {/* Calculator Button */}
        <Button
          size="icon"
          variant="outline"
          className="w-12 h-12 bg-card border-border shadow-lg hover:bg-muted"
          onClick={openCalculator}
          data-testid="button-calculator"
        >
          <Calculator className="w-5 h-5" />
        </Button>

        {/* Share Button */}
        <Button
          size="icon"
          variant="outline"
          className="w-12 h-12 bg-card border-border shadow-lg hover:bg-muted"
          onClick={handleShare}
          data-testid="button-share"
        >
          <Share className="w-5 h-5" />
        </Button>
      </div>
    </div>
  );
}

client/src/components/CombinedAnalysisPa.content:
import { useState, useEffect, useRef } from "react";
import { Card, CardHeader, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { X, Leaf, Factory, Zap, BarChart3, Target } from "lucide-react";
import type { SiteAnalysis } from "@/types/hydrogen";
import type { HydrogenSite } from "@/types/hydrogen";

interface CombinedAnalysisPanelProps {
  analysis?: SiteAnalysis;
  site?: HydrogenSite;
  onClose: () => void;
  defaultTab?: "suitability" | "sustainability";
}

export default function CombinedAnalysisPanel({ 
  analysis, 
  site, 
  onClose, 
  defaultTab = "suitability" 
}: CombinedAnalysisPanelProps) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstanceRef = useRef<any>(null);
  const [activeTab, setActiveTab] = useState(defaultTab);

  const getScoreColor = (score: number) => {
    if (score >= 80) return "text-green-600 dark:text-green-400";
    if (score >= 60) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const getScoreLabel = (score: number) => {
    if (score >= 80) return "Excellent Site ðŸŒŸ";
    if (score >= 60) return "Good Site âœ…";
    return "Poor Site âŒ";
  };

  const getScoreBgColor = (score: number) => {
    if (score >= 80) return "bg-green-100 dark:bg-green-900/20";
    if (score >= 60) return "bg-yellow-100 dark:bg-yellow-900/20";
    return "bg-red-100 dark:bg-red-900/20";
  };

  // Chart setup for sustainability tab
  useEffect(() => {
    if (activeTab !== "sustainability" || !site || !chartRef.current) return;

    // Import Chart.js dynamically
    import('chart.js/auto').then((Chart) => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }

      const ctx = chartRef.current;
      if (!ctx) return;

      chartInstanceRef.current = new Chart.default(ctx, {
        type: 'doughnut',
        data: {
          labels: ['Wind Energy', 'Solar Energy', 'Grid Power'],
          datasets: [{
            data: [
              site.renewableUtilization ? Math.floor(site.renewableUtilization * 0.65) : 45,
              site.renewableUtilization ? Math.floor(site.renewableUtilization * 0.35) : 25,
              site.renewableUtilization ? 100 - site.renewableUtilization : 30
            ],
            backgroundColor: [
              'hsl(158 64% 52%)',
              'hsl(48 96% 53%)',
              'hsl(240 5.9% 90%)'
            ],
            borderWidth: 0
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: true,
          plugins: {
            legend: {
              position: 'bottom',
              labels: {
                usePointStyle: true,
                boxWidth: 6,
                font: {
                  size: 10
                }
              }
            }
          }
        }
      });
    });

    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }
    };
  }, [site, activeTab]);

  if (!analysis && !site) return null;

  return (
    <Card className="absolute top-4 right-4 z-[1000] w-96 max-w-[calc(100vw-2rem)] bg-card border border-border shadow-lg md:w-96 sm:w-80">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="font-semibold text-foreground">Site Suitability</h3>
            {site && (
              <p className="text-sm text-muted-foreground mt-1" data-testid="text-site-name">
                {site.name}
              </p>
            )}
          </div>
          <Button
            size="icon"
            variant="ghost"
            onClick={onClose}
            className="w-6 h-6"
            data-testid="button-close-analysis-panel"
          >
            <X className="w-4 h-4" />
          </Button>
        </div>
      </CardHeader>

      <CardContent className="p-4">
        <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as "suitability" | "sustainability")}>
          {/* <TabsList className="grid w-full grid-cols-2 mb-4">
            <TabsTrigger value="suitability" className="flex items-center space-x-2" data-testid="tab-suitability">
              <Target className="w-4 h-4" />
              <span>Suitability</span>
            </TabsTrigger>
            <TabsTrigger value="sustainability" className="flex items-center space-x-2" data-testid="tab-sustainability">
              <Leaf className="w-4 h-4" />
              <span>Impact</span>
            </TabsTrigger>
          </TabsList> */}

          <TabsContent value="suitability" className="space-y-4">
            {analysis && (
              <>
                {/* Score Display */}
                <div className="flex items-center space-x-3">
                  <div className={`w-12 h-12 rounded-full flex items-center justify-center ${getScoreBgColor(analysis.suitabilityScore)}`}>
                    <span className={`font-bold text-lg ${getScoreColor(analysis.suitabilityScore)}`} data-testid="text-score-value">
                      {analysis.suitabilityScore}
                    </span>
                  </div>
                  <div>
                    <p className={`text-sm font-medium ${getScoreColor(analysis.suitabilityScore)}`} data-testid="text-score-label">
                      {getScoreLabel(analysis.suitabilityScore)}
                    </p>
                    <p className="text-xs text-muted-foreground">Out of 100</p>
                  </div>
                </div>

                {/* Factor Breakdown */}
                <div className="space-y-2">
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Renewable Access</span>
                    <span className="text-primary font-medium" data-testid="text-renewable-access">
                      {analysis.factors.renewableAccess}%
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Transport Cost</span>
                    <span className="text-accent font-medium" data-testid="text-transport-cost">
                      {analysis.factors.transportCost}
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Demand Proximity</span>
                    <span className="text-secondary font-medium" data-testid="text-demand-proximity">
                      {analysis.factors.demandProximity}
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Water Availability</span>
                    <span className="text-primary font-medium" data-testid="text-water-availability">
                      {analysis.factors.waterAvailability}
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Regulatory Support</span>
                    <span className="text-muted-foreground font-medium" data-testid="text-regulatory-support">
                      {analysis.factors.regulatorySupport}
                    </span>
                  </div>
                </div>

                {/* Quick Impact Metrics */}
                <div className="pt-2 border-t border-border space-y-2">
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Est. COâ‚‚ Savings</span>
                    <span className="text-green-600 dark:text-green-400 font-medium" data-testid="text-co2-savings">
                      {(analysis.co2SavedAnnually / 1000).toFixed(0)}kt/year
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Industries Supported</span>
                    <span className="text-secondary font-medium" data-testid="text-industries-supported">
                      {analysis.industriesSupported}
                    </span>
                  </div>
                  <div className="flex justify-between text-xs">
                    <span className="text-muted-foreground">Renewable Utilization</span>
                    <span className="text-accent font-medium" data-testid="text-renewable-utilization">
                      {analysis.renewableUtilization}%
                    </span>
                  </div>
                </div>

                {/* Recommendations */}
                {analysis.recommendations.length > 0 && (
                  <div className="pt-2 border-t border-border">
                    <p className="text-xs text-muted-foreground mb-1">Recommendations:</p>
                    <ul className="text-xs space-y-1">
                      {analysis.recommendations.slice(0, 2).map((rec, index) => (
                        <li key={index} className="text-foreground">â€¢ {rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </>
            )}
          </TabsContent>

          <TabsContent value="sustainability" className="space-y-4">
            {site && (
              <>
                {/* CO2 Savings */}
                <div className="bg-muted rounded-lg p-3">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-foreground">COâ‚‚ Saved Annually</span>
                    <Leaf className="w-4 h-4 text-primary" />
                  </div>
                  <div className="text-2xl font-bold text-primary" data-testid="text-co2-annual">
                    {site.co2SavedAnnually ? (site.co2SavedAnnually / 1000).toFixed(0) : '25'}k
                  </div>
                  <div className="text-xs text-muted-foreground">tons per year</div>
                </div>

                {/* Industries Supported */}
                <div className="bg-muted rounded-lg p-3">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-foreground">Industries Supported</span>
                    <Factory className="w-4 h-4 text-secondary" />
                  </div>
                  <div className="text-2xl font-bold text-secondary" data-testid="text-industries-count">
                    {site.industriesSupported || 8}
                  </div>
                  <div className="text-xs text-muted-foreground">steel, transport, chemical</div>
                </div>

                {/* Renewable Utilization */}
                <div className="bg-muted rounded-lg p-3">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-foreground">Renewable Utilization</span>
                    <Zap className="w-4 h-4 text-accent" />
                  </div>
                  <div className="text-2xl font-bold text-accent" data-testid="text-renewable-percent">
                    {site.renewableUtilization || 75}%
                  </div>
                  <div className="text-xs text-muted-foreground">wind + solar mix</div>
                </div>

                {/* Chart Container */}
                <div className="h-32 bg-muted rounded-lg p-3">
                  <canvas ref={chartRef} data-testid="chart-energy-mix"></canvas>
                </div>

                {/* Additional Metrics */}
                <div className="grid grid-cols-2 gap-3 pt-2 border-t border-border">
                  <div className="text-center">
                    <div className="text-lg font-bold text-foreground" data-testid="text-suitability-score">
                      {site.suitabilityScore}/100
                    </div>
                    <div className="text-xs text-muted-foreground">Suitability Score</div>
                  </div>
                  <div className="text-center">
                    <div className="text-lg font-bold text-foreground" data-testid="text-economic-impact">
                      $2.5M
                    </div>
                    <div className="text-xs text-muted-foreground">Economic Impact</div>
                  </div>
                </div>
              </>
            )}
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

